import FroalaEditor from '../../src';
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
const Bookwrite = () => {
  return (
    <div>
      
      <FroalaEditor
        base='https://cdnjs.cloudflare.com/ajax/libs/froala-editor/2.3.4'
        ref={(r) => {this.froalaEditor = r;}}
        value={str}
        alignP
        charCounterP
        codeViewP
        codeBeautifierP
        colorsP
        draggableP
        emoticonsP
        entitiesP
        fileP
        fontFamilyP
        fontSizeP
