import React from "react";
import './books.scss';
import { Col, Row } from "reactstrap";
import Widget from "../../components/Widget/Widget";
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import FroalaEditor from "react-froala-editor";
import 'froala-editor/css/froala_style.min.css';
import 'froala-editor/css/froala_editor.pkgd.min.css';
import 'froala-editor/js/plugins.pkgd.min.js';
import FroalaEditorComponent from 'react-froala-wysiwyg';

import Carousel from 'nuka-carousel';

// import Carousel from "react-multi-carousel";
// import "react-multi-carousel/lib/styles.css";


var downloadGrid = (function(){
  
  "use strict";
  
  var $cardContainer = $('.download-cards');
  var $downloadCard  = $('.download-card__content-box');
  var $viewTrigger   = $('button').attr('data', 'trigger');

  function swapTriggerActiveClass(e) {   
    $viewTrigger.removeClass('active');
    $(e.target).addClass('active');
  }

  function swapView(e) {
    var $currentView = $(e.target).attr('data-trigger');
    $cardContainer.attr('data-view', $currentView);
  }

  $(document).ready(function(){
    // Event Listener
    $viewTrigger.click(function(e){
      swapTriggerActiveClass(e);
      swapView(e);        
    });  
  });
  
})();


const Books = () => {
  const str = "<p>React Froala Editor</p>";
  return (
    <div className="container">
    <div className="dc-view-switcher">
      <button data-trigger="grid-view" />
      <button data-trigger="list-view" className="active" />
    </div>
    <div data-view="list-view" className="download-cards">
      <article className="download-card">
        <div className="download-card__icon-box"><img src="https://bytesizemoments.com/wp-content/uploads/2014/04/placeholder3.png" /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">Photos</h2>
            <h3 className="download-card__content-box__title">Tool Safety Products</h3>
            <p className="download-card__content-box__description">High &amp; low-res photos for print and web media. </p>
            <a className="button"> <i className="fa fa-cloud-download" /> Download</a>
          </div>
        </div>
      </article>
      <article className="download-card">
        <div className="download-card__icon-box"><img src="https://bytesizemoments.com/wp-content/uploads/2014/04/placeholder3.png" /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">PDF</h2>
            <h3 className="download-card__content-box__title">Tool Safety Catalogues</h3>
            <p className="download-card__content-box__description">Print and web ready PDF's. </p>
            <a className="button"> <i className="fa fa-cloud-download" /> Download</a>
          </div>
        </div>
      </article>
      <article className="download-card">
        <div className="download-card__icon-box"><img src="https://bytesizemoments.com/wp-content/uploads/2014/04/placeholder3.png" /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">Photos</h2>
            <h3 className="download-card__content-box__title">Height Safety Products</h3>
            <p className="download-card__content-box__description">High &amp; low-res photos for print and web media. </p>
            <a className="button"> <i className="fa fa-cloud-download" /> Download</a>
          </div>
        </div>
      </article>
      <article className="download-card">
        <div className="download-card__icon-box"><img src="https://bytesizemoments.com/wp-content/uploads/2014/04/placeholder3.png" /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">Document</h2>
            <h3 className="download-card__content-box__title">Course Booking Form</h3>
            <p className="download-card__content-box__description">Word document </p>
            <a className="button"> <i className="fa fa-cloud-download" /> Download</a>
          </div>
        </div>
      </article>
      <article className="download-card">
        <div className="download-card__icon-box"><img src="https://bytesizemoments.com/wp-content/uploads/2014/04/placeholder3.png" /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">Photos</h2>
            <h3 className="download-card__content-box__title">Height Safety Products</h3>
            <p className="download-card__content-box__description">High &amp; low-res photos for print and web media. </p>
            <a className="button"> <i className="fa fa-cloud-download" /> Download</a>
          </div>
        </div>
      </article>
      <article className="download-card">
        <div className="download-card__icon-box"><img src="https://bytesizemoments.com/wp-content/uploads/2014/04/placeholder3.png" /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">Photos</h2>
            <h3 className="download-card__content-box__title">Height Safety Products</h3>
            <p className="download-card__content-box__description">High &amp; low-res photos for print and web media. </p>
            <a className="button"> <i className="fa fa-cloud-download" /> Download</a>
          </div>
        </div>
      </article>
    </div>
  </div>


  );
};

export default Books;
