import React, { useState } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import classnames from "classnames";
import {
  Col,
  Row,
  Button,
} from "reactstrap";
import Widget from "../../components/Widget/Widget.js";
import Code from "../../components/Code/Code.js";
import Notification from "../../components/Notification/Notification.js";
import s from "./Notifications.module.scss";
import successIcon from "../../assets/notificationsIcons/successIcon.svg";
import closeIcon from "../../assets/notificationsIcons/closeIcon.svg";

const positions = [
  toast.POSITION.TOP_LEFT,
  toast.POSITION.TOP_CENTER,
  toast.POSITION.TOP_RIGHT,
  toast.POSITION.BOTTOM_LEFT,
  toast.POSITION.BOTTOM_CENTER,
  toast.POSITION.BOTTOM_RIGHT
];

const notificationTypes = ["info", "success", "warning", "error"]


class TabPanel extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedTabIndex: props.children.length == 0 ? null : 0
    };

    this._handleClick = this._handleClick.bind(this);
  }

  _handleClick(index) {
    this.setState({
      selectedTabIndex: index
    });
    
    this.props.onTabChange(index);
  }

  _renderTabs() {
    let tabs = [];
    
    for (let i = 0; i < this.props.children.length; i++) {
      tabs.push(
        <Tab
          key={`tab-${i}`}
          label={this.props.children[i].props.title}
          index={i}
          isSelected={i === this.state.selectedTabIndex}
          onClick={this._handleClick} />
      );
    }

    return tabs;
  }
  
  _renderTabContent() {
    return this.props.children[this.state.selectedTabIndex].props.children;
  }

  render() {
    if (this.state.selectedTabIndex == null) {
      return 'nope';
    }
    
    return (
      <div className="tab-panel">
        <div className="tab-panel__header">
          {this._renderTabs()}
        </div>
        <div className="tab-panel__content">
          {this._renderTabContent()}
        </div>
      </div>
   );
  }
}

class Tab extends React.Component {
  constructor(props) {
    super(props);

    this._handleClick = this._handleClick.bind(this);
  }

  _handleClick() {
    this.props.onClick(this.props.index);
  }

  render() {
    const tabClassName = this.props.isSelected ? 'tab tab--selected' : 'tab';

    return (
      <span className={tabClassName} onClick={this._handleClick}>
        <span className="tab__label">{this.props.label}</span>
      </span>
    );
  }
}

class Counter extends React.Component {
  constructor(props) {
    super(props);
    
    this.state = {
      count: 0
    }
    
    this._intervalId = null;
  }
  
  componentDidMount() {
    this._interValId = setInterval(() => {
      this.setState({
        count: this.state.count + 1
      });
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this._intervalId);
  }

  render() {
    return (
      <div>{this.state.count}</div>
    )
  }
}


const Notifications = function () {



  return (
    <div>
    <TabPanel >
        <div title="Tab 1">
          <p>this is tab 1 content</p>
        </div>
        <div title="Tab 2">
          <h1>this is tab 2 content</h1>
        </div>
        <div title="Tab 3">
          <Counter />
        </div>
      </TabPanel>
    </div>
  )
}

export default Notifications;
