import Modal from "./Modal";
import './books.scss';
import { Col, Row } from "reactstrap";
import Widget from "../../components/Widget/Widget";
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import FroalaEditor from "react-froala-editor";
import 'froala-editor/css/froala_style.min.css';
import 'froala-editor/css/froala_editor.pkgd.min.css';
import 'froala-editor/js/plugins.pkgd.min.js';
const axios = require('axios');


// const customStyles = {
//   content: {
//     top: '50%',
//     left: '50%',
//     right: 'auto',
//     bottom: 'auto',
//     marginRight: '-50%',
//     transform: 'translate(-50%, -50%)',
//   },
// };

// const styles = {
//   content: {
//          top: '50%',
//          left: '50%',
//          right: 'auto',
//          bottom: 'auto',
//          marginRight: '-50%',
//          transform: 'translate(-50%, -50%)',
//        },
//   fontFamily: "sans-serif",
//   textAlign: "center"
// };

const modalProps = {
  triggerText: "Launch the Modal!"
};
const modalContent = (
  <React.Fragment>
    <p>
      Press <code>Esc</code> or click Outside the Modal to exit.
    </p>
    <p>
      Pressing Return also exits the Modal if you haven't changed the focus!
    </p>
  </React.Fragment>
);

const Books = () => {


  
  const [booksList, setBooklist] = useState([]);
  const [id, setId] = React.useState(1);
  React.useEffect(() => {
    if (id == null || id === '') {
      return;
    }
    
    axios.get('https://api.itbook.store/1.0/search/reaction')
  .then(function (response) {
    // handle success
    console.log('response from book api:',response.data.books);
    setBooklist(response.data.books)
    // booksList=response.data.books;
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  }, [id]);

  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    //subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpen(false);
  }




  const str = "<p>React Froala Editor</p>";
  return (
    <div className="container">
 
 <Modal modalProps={this.modalProps} modalContent={this.modalContent} />

 {/* <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        
        <button onClick={closeModal}>close</button>
        <div>I am a modal</div>
      
      </Modal> */}

{/* 
<Modal isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={styles}>
          <h2>Simple centered modal</h2>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam
            pulvinar risus non risus hendrerit venenatis. Pellentesque sit amet
            hendrerit risus, sed porttitor quam.
          </p>
        </Modal> */}


    <div data-view="list-view" className="download-cards">
    {booksList.map((item)=>
      <article className="download-card">
        <div className="download-card__icon-box"><img src={item.image} /></div>
        <div className="download-card__content-box">
          <div className="content">
            <h2 className="download-card__content-box__catagory">{item.price}</h2>
            <h3 className="download-card__content-box__title">{item.title}</h3>
            <p className="download-card__content-box__description">High &amp; low-res photos for print and web media. </p>
            <a onClick={openModal} className="button"> <i className="fa fa-book" /> Read</a>
          </div>
        </div>
      </article>



    )}
 




    </div>
  </div>


  );
};

export default Books;
